class FormPrompt extends SelectPrompt {

  constructor(options) {

    super({ ...options, multiple: true });

    this.type = 'form';

    this.initial = this.options.initial;

    this.align = [this.options.align, 'right'].find(v => v != null);

    this.emptyError = '';

    this.values = {};

  }
