EventEmitter.prototype.listenerCount = listenerCount;

function listenerCount(type) {

  var events = this._events;



  if (events) {

    var evlistener = events[type];



    if (typeof evlistener === 'function') {

      return 1;

    } else if (evlistener) {

      return evlistener.length;

    }

  }



  return 0;

}
