def isatty(self):
    stream = self.__wrapped
    if 'PYCHARM_HOSTED' in os.environ:
        if stream is not None and (stream is sys.__stdout__ or stream is sys.__stderr__):
            return True
    try:
        stream_isatty = stream.isatty
    except AttributeError:
        return False
    else:
        return stream_isatty()
