prefix = f"{prefix}_"
len_prefix = len(prefix)

for key in sorted(os.environ):
    if not key.startswith(prefix):
        continue

    value = os.environ[key]

    try:
        value = loads(value)
    except Exception:

        pass


    key = key[len_prefix:]

    if "__" not in key:

        self[key] = value
        continue


    current = self
    *parts, tail = key.split("__")

    for part in parts:

        if part not in current:
            current[part] = {}

        current = current[part]

    current[tail] = value

return True
