def run(self):

    conn = self.database.connection()

    try:

        while True:

            try:

                if conn is None:  # Paused.

                    if self.wait_unpause():

                        conn = self.database.connection()

                else:

                    conn = self.loop(conn)

            except ShutdownException:

                logger.info('writer received shutdown request, exiting.')

                return

    finally:

        if conn is not None:

            self.database._close(conn)

            self.database._state.reset()
