def _weekday_index(day: str) -> int:

    weekdays = (

        "monday",

        "tuesday",

        "wednesday",

        "thursday",

        "friday",

        "saturday",

        "sunday",

    )

    if day not in weekdays:

        raise ScheduleValueError(

            "Invalid start day (valid start days are {})".format(weekdays)

        )

    return weekdays.index(day)
