def banner(self):
    banner = self.banner1
    if self.profile and self.profile != 'default':
        banner += '\nIPython profile: %s\n' % self.profile
    if self.banner2:
        banner += '\n' + self.banner2
    elif self.enable_tip:
        banner += "Tip: {tip}\n".format(tip=pick_tip())
    return banner

def show_banner(self, banner=None):
    if banner is None:
        banner = self.banner
    print(banner)

