def cached(timeout=100):

    def _cached(func):

        @wraps(func)

        def with_caching(*args, **kwargs):

            key = id(func)

            for arg in args:

                key += id(arg)

            for k, v in kwargs:

                key += id(k) + id(v)



            # If it was cached and the cache timeout hasn't been reached

            if key in _cache and time() - _cache[key][0] < timeout:

                return _cache[key][1]



            with _cache_lock:

                result = func(*args, **kwargs)

                _cache[key] = (time(), result)



            return result



        return with_caching



    return _cached
