pid = os.getpid()


filename = f"/proc/{pid}/cgroup"

cgroup = None
try:
    with open(filename, "r") as f :



        lines = f.readlines()
        for line in lines:
            tokens = line.rstrip("\r\n").split(":")
            if len(tokens) < 3:
                continue
            if tokens[1] == "cpuset":
                cgroup = tokens[2]
except OSError:
    pass

if cgroup is None:
    return -1

size = -1
filename = f"/sys/fs/cgroup/cpuset/{cgroup}/cpuset.cpus"
try:
    with open(filename, "r") as f :
        value = f.read().rstrip("\r\n")
        nlist = parseNumericList(value)
        size = len(nlist)

except OSError:
    pass

return size
