def get_password(self):
    self.screen.timeout(-1)  # disable the screen timeout
    curses.noecho()
    password = self.screen.getstr(9, self.startcol + 6, 60)
    self.screen.timeout(100)  # restore the screen timeout
    return password.decode("utf-8")
