@functools.wraps(processor)
def wrapper(*args, **kwargs) -> str:
    try:
        output = processor(*args, **kwargs)
    except ParseError:
        output = None
    if isinstance(output, (str, int, float)):
        return str(output)
    else:
        raise ParseError('Cannot use complex JSON value types with --form/--multipart.')

return wrapper
