def stereo_volume(get_frame, t):
    frame = get_frame(t)
    if len(frame) == 1:  
        frame *= left if left is not None else right
    else:  
        for i in range(len(frame[0])):  
            frame[:, i] *= left if i % 2 == 0 else right
    return frame

