_replaceConstants(constants, tree) {

  for (const id in tree) {

    const node = tree[id];

    switch (typeof node) {

      case 'object':

        if (node.constructor.name.match(/Stream/)) {

          continue;

        }

        this._replaceConstants(constants, node);

        break;

      case 'string':

        if (node.charAt(0) === '@') {

          tree[id] = constants[node];

        }

    }

  }

}
