public <T> void write(WebSocketEvent<T> event) {
    try {
        String message = JSON.toJSONString(event, FastJsonSerializer.serializeConfig);
        if(session.isOpen()){
            synchronized (session){
                session.getBasicRemote().sendText(message);
            }
        }
    } catch (Throwable ignored) {
    }
}
