public Object readObject() {
    if (context == null) {
        return parser.parse();
    }

    readBefore();
    Object object;
    switch (context.state) {
        case StartObject:
        case PropertyValue:
            object = parser.parseKey();
            break;
        default:
            object = parser.parse();
            break;
    }

    readAfter();
    return object;
}
