public static boolean isPath(Class<?> clazz) {
    if (pathClass == null && !pathClass_error) {
        try {
            pathClass = Class.forName("java.nio.file.Path");
        } catch (Throwable ex) {
            pathClass_error = true;
        }
    }
    if (pathClass != null) {
        return pathClass.isAssignableFrom(clazz);
    }
    return false;
}
