public static void setParams(String params) {
    if (StringUtil.isNotEmpty(params)) {
        String[] ps = params.split("[;|,|&]");
        for (String s : ps) {
            String[] ss = s.split("[=|:]");
            if (ss.length == 2) {
                PARAMS.put(ss[0], ss[1]);
            }
        }
    }
}
