remove(transport) {

  if (!transport) return this;

  let target = transport;

  if (!isStream(transport) || transport.log.length > 2) {

    target = this.transports.filter(

      match => match.transport === transport

    )[0];

  }



  if (target) {

    this.unpipe(target);

  }

  return this;

}
