new Promise((resolve) => {
  fs.createReadStream(path)
    .pipe(new PNG())
    .on('parsed', function Parsed() {
      /** @type {ImageData} */
      const imageData = {
        colorSpace: 'srgb',
        width: this.width,
        height: this.height,
        data: this.data,
      };
      resolve(imageData);
    });
})
