static fromHTMLTable(element: HTMLElement): Header {

  const header = new Header();

  const thead = element.querySelector('thead');

  const ths = thead.querySelectorAll('th');



  for (const th of ths as any) {

    header.columns.push({

      name: th.innerHTML,

      width: th.width,

    });

  }



  return header;

}
