import { Readable } from 'stream'



function getStdIn(stdin: Readable): Promise<string> {

  let out = ''

  return new Promise((resolve) => {

    stdin

      .setEncoding('utf8')

      .on('readable', () => {

        let current

        while ((current = stdin.read())) {

          out += current

        }

      })

      .on('end', () => resolve(out.trim()))

    setTimeout(() => {

      if (!out.trim()) {

        resolve(out.trim())

      }

    }, 1000)

  })

}
