    } catch (e) {
      if (!zipFsDir) throw e
    }
    const seen = new Set()
    const nextDirent = () => {
      const entry = realFsDir?.readSync() || zipFsDir?.readSync()
      if (entry && !seen.has(entry.name)) {
        seen.add(entry.name)
        return entry
      }
      return null
    }

    const onClose = () => {
      zipFsDir?.closeSync()
      realFsDir?.closeSync()
    }

    return new CustomDir(p, nextDirent, { onClose })
