setTime(time: CronTime) {

	if (!(time instanceof CronTime)) {

		throw new CronError('time must be an instance of CronTime.');

	}



	const wasRunning = this.running;

	this.stop();



	this.cronTime = time;

	if (time.realDate) this.runOnce = true;



	if (wasRunning) this.start();

}
