emit<EventName extends keyof EventTypes>(
  event: EventName,
  ...args: EventArgs<EventTypes[EventName]>
): boolean {
  const eventName = event as string;

  this.init(eventName);

  if (this.callbacks[eventName].length > 0) {
    this.callbacks[eventName].forEach((value) => value(...args));
    return true;
  }

  return false;
}
