def back(self, back=None, light=False, on_stderr=False):
    if back is None:
        back = self._default_back
    self._back = back
    # Emulate LIGHT_EX with BRIGHT_BACKGROUND Style
    if light:
        self._light |= WinStyle.BRIGHT_BACKGROUND
    else:
        self._light &= ~WinStyle.BRIGHT_BACKGROUND
    self.set_console(on_stderr=on_stderr)
